import React from 'react';
import './resources.css'

export function Categories(props) {
    const [clubs, setClubs] = React.useState([])
    const [filteredClubs, setFilteredClubs] = React.useState([])

    React.useEffect(() => {
        axios.get("http://localhost:3001")
          .then((response) => {
            const results = response.data.values;
            results.splice(0, 1);
            setClubs(results);
            setFilteredClubs(results)
          })
      }, []);

      /**
       * Callback to handle typing (onChange) of the
       * search inpout field
       */
      const handleSearch = (event) => {
        const search = event.target.value
        if (search) {
          setFilteredClubs(clubs.filter((club) =>
            club[2].toLowerCase().includes(search.toLowerCase())
            || club[4].toLowerCase().includes(search.toLowerCase())))
        } else {
          setFilteredClubs(clubs)
        }
      }
      // {/* For category button */}
      const handleClick = (event) => {
        const search = event.target.value
        if (search) {
          setFilteredClubs(clubs.filter((club) => 
          club[7].toLowerCase().includes(search.toLowerCase())))
        } else {
          setFilteredClubs(clubs)
        }
      }




    var btn = document.getElementById("btn");
    var categoryNames = ["Academic", "Activism", "Affinity Groups", "Community Service", "Hobbies & Interests", "Language & Culture", "Sports & Fitness", "STEM", "Visual & Performing Arts", "Writing & Literature"]; 
    function categories() {
        for(var i = 0; i < categoryNames.length; i+=2) {
            var option = document.createElement("button");
            option.innerHTML = categoryNames[i];
            // option.addEventListener("click", handleClick)
            document.body.appendChild(option);
        }
    }
    return (
        <div>
            { categories() }
        </div>
    );
}

export default Categories;
